$j(document).ready(function() {

	$j('.heightForAge').hide();
	$j('.weightForAge').hide();
	$j('.heightWeight').hide();
	$j('.lastHeight').hide();

	var lastHeight = "";
	var lastHeightDate = "";

	var birthDate = $j('.birthdate').text();
	var date = getValue('encounter.value');

	var months = monthDiff(birthDate, date);

	if(months > 228)
	{
		$j('.lastHeight').show();
	}

	getField('height.value').change(function() {

		var patientId = $j('[name=personId]').val();
		var gender = $j('.gender').text();

		var length = getValue("height.value");

		if(date != null)
		{ 
			if(months > 228)
			{
				var weight = getValue("weight.value");
				if(length != null && length > 0 && weight != null && weight > 0)
				{
					var bsa = calculateBSA(length, weight);

					setValue('bsaVal.value', bsa);
					$j('.bsaValue').html(bsa);
					$j('.bsa').show();
				}
				else if (lastHeight != null && lastHeight > 0 && weight != null && weight > 0)
				{
					var monthsLastHeight = monthDiff(lastHeightDate, birthDate);
					if(monthsLastHeight > 228)
					{
						var bsa = calculateBSA(lastHeight, weight);

						setValue('bsaVal.value', bsa);
						$j('.bsaValue').html(bsa);
						$j('.bsa').show();	
					}
				}
			}
			else 
			{
				var weight = getValue("weight.value");
				if(length != null && length > 0 && weight != null && weight > 0)
				{
					var bsa = calculateBSA(length, weight);

					setValue('bsaVal.value', bsa);
					$j('.bsaValue').html(bsa);
					$j('.bsa').show();
				}
			}
		}
	});

	getField('weight.value').change(function() {
		var patientId = $j('[name=personId]').val();


		var gender = $j('.gender').text();
		var weight = getValue("weight.value");

		if(date != null)
		{ 
			if(months > 228)
			{
				var length = getValue("height.value");

				if(length != null && weight != null && length > 0 && weight > 0)
				{
					var bsa = calculateBSA(length, weight);

					setValue('bsaVal.value', bsa);
					$j('.bsaValue').html(bsa);
					$j('.bsa').show();
				}
				else if (weight != null && lastHeight != null && lastHeight > 0 && weight > 0)
				{
					var monthsLastHeight = monthDiff(birthDate, lastHeightDate);

					if(monthsLastHeight > 228)
					{

						var bsa = calculateBSA(lastHeight, weight);

						setValue('bsaVal.value', bsa);
						$j('.bsaValue').html(bsa);
						$j('.bsa').show();	
					}
				}
			}
			else {
				var length = getValue("height.value");
				if(length != null && weight != null && length > 0 && weight > 0)
				{
					var bsa = calculateBSA(length, weight);

					setValue('bsaVal.value', bsa);
					$j('.bsaValue').html(bsa);
					$j('.bsa').show();
				}
			}
		}
	});

		getField('encounter.value').change(function() {

			var patientId = $j('[name=personId]').val();

			var gender = $j('.gender').text();

			date = getValue("encounter.value");
			var length = getValue("height.value");
			var weight = getValue("weight.value");


			months = monthDiff(birthDate, date);
			if(months < 229)
			{
				$j('.lastHeight').hide();
			}


			if(weight != null && weight > 0)
			{ 			
				if(months > 228)
				{
					var length = getValue("height.value");
					if(length != null)
					{
						var bsa = calculateBSA(length, weight);

						setValue('bsaVal.value', bsa);
						$j('.bsaValue').html(bsa);
						$j('.bsa').show();
					}
					else if (lastHeight != null)
					{
						var monthsLastHeight = monthDiff(lastHeightDate, birthDate);
						if(monthsLastHeight > 228)
						{
							var bsa = calculateBSA(lastHeight, weight);

							setValue('bsaVal.value', bsa);
							$j('.bsaValue').html(bsa);
							$j('.bsa').show();	
						}
					}
				}
				else {
					var length = getValue("height.value");
					if(length != null)
					{
						var bsa = calculateBSA(length, weight);

						setValue('bsaVal.value', bsa);
						$j('.bsaValue').html(bsa);
						$j('.bsa').show();
					}
				}
			}
		});
	});



	function monthDiff(date1, date2) {
	    var months;

	    var d1 = new Date();
	    d1.setTime(Date.parse(date1));

	    var d2 = new Date();
	    d2.setTime(Date.parse(date2));

	    months = (d2.getFullYear() - d1.getFullYear()) * 12;
	    if(d2.getMonth() > d1.getMonth())
	    {
	    	months = months + (d2.getMonth() - d1.getMonth());
	    }
	    else if(d1.getMonth() > d2.getMonth())
	    {
	    	months = months - (d1.getMonth() - d2.getMonth());
	    }
	   	if(d1.getDate() > d2.getDate())
	   	{
	   		months = months - 1;	
	   	}

	    return months;
	}

	function calculateBSA(length, weight) {
	    var bsa;

	    bsa = (length*weight)/3600
	    bsa = Math.sqrt(bsa);

	    bsa = parseFloat(bsa).toFixed(2);

	    return bsa;
	}





