group.find("#checkboxAll").find("input").click(
/*  This was tricky... A number of things needed to happen
Basically, This is supposed to treat a group of inputs as if
were all one big checkbox. It is designed so that a checkbox
can be next to an input, and the user clicks the input, the
checkbox checks as well. But, when the user clicks the checkbox,
the browser marks the checkbox as checked. Therefore, when we check
if the checkbox is already checked, it always respondes true...
We needed to have 2 cases: when the clicking action is on the first checkbox
and when the action is on any other.  */


function(){

var flip;
var checked = $j(this).siblings(":checkbox:first").attr("checked");
if($j(this).attr("name") == $j(this).parents("#checkboxAll:first").find(":checkbox:first").attr("name")){
checked = $j(this).attr("checked");
flip = checked;
}else{
flip = !checked;
}

if($j(this).attr("type") == "text") if(flip == false) flip = !flip; // this is so the user doesn't go to check the checkbox, then uncheck it when they hit the input.





$j(this).parents("#checkboxAll:first").find(":checkbox").attr("checked",flip);
$j(this).parents("#checkboxAll:first").find(":checkbox").change();

var sbp1 = getValue('sbpid.value');
var dbp1 = getValue('dbpid.value');

var second =group.find("#checkboxCheckbox:first").find(":checkbox:first").attr("checked");
var thard =group.find("#checkboxCheckbox2:first").find(":checkbox:first").attr("checked");
var first =group.find("#checkboxAll:first").find(":checkbox:first").attr("checked");

if(first=='checked' || thard=='checked' || second=='checked'){
$j('#message').text('ORANGE Less than 10 min').css('color','red').css("font-weight","Bold");
$j('#dangertd').css('background-color','orange');
} else if(thard=='checked'  &&( (sbp > 139 ) || (dbp > 89 ))) {

$j('#message').html('Shout for help to mobilize the team and call for emergency equipment. </br\>  If you are alone, the family may be your team. </br\> Airway: Turn woman onto her side to prevent aspiration. Ensure her airway is open. </br\> If available, give oxygen at 4–6 L per minute by mask or cannula. </br\> Breathing: If the woman is not breathing, begin ventilation with a bag and mask. </br\> Circulation: If pulse is absent, begin cardiac massage6. Protect from injury, but do not actively restrain her. </br\> Do not put anything in her mouth. </br\>  Do not leave her alone. </br\> While giving care, ask the family about her medical history and recent signs of illness including fever, chills, headache, and neck stiffness. </br\> Put up an intravenous line and transfer urgently to hospital </br\> ').css('color','red').css("font-weight","Bold");
$j('#dangertd').css('background-color','orange');

} else{
$j('#message').text(' ').css('color','red').css("font-weight","Bold");
$j('#dangertd').css('background-color','white');
}
});

