group.find("#all").find("input:checkbox[type='checkbox']").click(function(){
if ($j(this).is(":checked")){
var scr = $j(this).attr('value');
var tscr = $j('#score').html();
var setscr = parseInt(scr) + parseInt(tscr);
$j('#score').html(setscr);
var r=$j('#score').html(setscr);
$j('#score1').html(r);
}
else {
var scr = $j(this).attr('value');
var tscr = $j('#score').html();
var setscr = parseInt(tscr) - parseInt(scr);
$j('#score').html(setscr);
}
});
});
});
