}

if(txt == null || txt == ''){
group2.find("input[type$='checkbox']").attr("checked",false);
group3.find("input[type$='checkbox']").attr("checked",false);
}
else if(txt > 5){
group3.find("input[type$='checkbox']").attr("checked",true);
}
else{
group3.find("input[type$='checkbox']").attr("checked",false);
}
});



});
