htmlform.script:
var $ = jQuery.noConflict();
$j(document).ready(function () {

$("#bweight input").change(function () {
var bbweight = $("#bweight input").val();
if (!isNaN(bbweight)) {
if (bbweight < 2500)
alert(bbweight + " is less than 2500 g");
if (bbweight > 2500 )
alert(bbweight + " is greater than 2500 g");
if (bbweight == 2500)
alert(bbweight + " is equal to 2500 g");
}

});

//$("#location select").attr("disabled",true);
$('#twin').hide();
//$("#provider select").attr("disabled",true);
$("#multiple_birth_check").change(function () {

if ($('#multiple_birth_check input:nth(0)').is(':checked')) {
$('#twin').show();
//alert("changed");
}

else {
$('#twin').hide();
}


});

});
