$hfe('button.addEntry')
.live(
"click",
function () {
var correctedAddButtonId = parseFloat(this.id) + 1;
var contentAddId = "#"
+ correctedAddButtonId
+ "-toggleContainer";
$hfe(contentAddId).toggle(true);
$hfe('#' + this.id).toggle(false);
$hfe(
'#' + parseFloat(this.id)
+ '-removeEntry')
.toggle(false);
return false;
});
//});

