//function() {
$hfe('button.removeEntry')
.live(
"click",
function () {
var correctedRemoveButtonId = parseFloat(this.id) - 1;
var contentAddId = "#"
+ parseFloat(this.id)
+ "-toggleContainer";
$hfe(contentAddId).toggle(false);
$hfe(':input:not(:button)',
contentAddId).val([]);
$hfe(
'#'
+ correctedRemoveButtonId
+ '-addEntry')
.toggle(true);
$hfe(
'#'
+ correctedRemoveButtonId
+ '-removeEntry')
.toggle(true);

return false;
});

