'span#hiv_status input[type=radio]:checked')
.val() == 703) {
$hfe(
'span#is_opportunistic input[type=checkbox]')
.attr('disabled', false);
} else {
$hfe(
'span#is_opportunistic input[type=checkbox]')
.attr('disabled', true);
}
});
// Setting other non-coded concept available...
$hfe('#other_motive').hide();
$hfe('#cons_motive').change(
function () {
