$hfe("input[type=radio][name=normal_1]")
.change(
function () {
var answer = $hfe(this).val();
if (answer == "normal") {
$hfe("span#pulmonary textarea")
.val("Normal");
$hfe("#pulmonary")
.disableSelection();
} else {
$hfe("span#pulmonary textarea")
.val(
"Abnormal because : ");
$hfe("#pulmonary")
.enableSelection();
$hfe("span#pulmonary textarea")
.focus();
}
});

