select
	(select name from location where location_id in (select parent_location from location where name like "%ANC%" and retired =0) limit 1 ) as location,
	IFNULL((select SUM(value) from ready_report_indicator where name="ANC New Registrations"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as ANCRegistration,
	IFNULL((select SUM(value) from ready_report_indicator where name="ANC new registrations under 14 years"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as ageUnder14ANCRegistration,
	IFNULL((select SUM(value) from ready_report_indicator where name="ANC new registrations aged 14-17 years"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as ageBetween14And17ANCRegistration,
	IFNULL((select SUM(value) from ready_report_indicator where name="ANC new registrations aged 18-19 years"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as agebtwn18and19ANCRegistration,
	IFNULL((select SUM(value) from ready_report_indicator where name="ANC First standard contacts (Within 12 Weeks)"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as firstContactANCRegistration,
	IFNULL((select SUM(value) from ready_report_indicator where name="ANC 4 contacts"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as ANC4Contacts,
	IFNULL((select SUM(value) from ready_report_indicator where name="ANC 8 contacts"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as ANC8Contacts,
	IFNULL((select SUM(value) from ready_report_indicator where name="Pregnant woman with previous scar referred to Hospital at last ANC (in last trimester )"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as ANCPregnantwithScarReferred,
	IFNULL((select SUM(value) from ready_report_indicator where name="ANC TT 1 given"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as ANCTT1given,
	IFNULL((select SUM(value) from ready_report_indicator where name="ANC TT 2 to 5 given"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as ANCbtwTT2andTT5given,
	IFNULL((select SUM(value) from ready_report_indicator where name="ANC TT new registrations fully vaccinated"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as ANCTTNewRegFullyVaccinated,
	IFNULL((select SUM(value) from ready_report_indicator where name="ANC new registrations who received full course of Iron and Folic Acid supplements (90 tablets)"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as ANCTTNewRegIronAndFAS,
	IFNULL((select SUM(value) from ready_report_indicator where name="ANC Insecticide Treated Bed nets distributed"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as ANCInsecticideTreatedBnetsDist,
	IFNULL((select SUM(value) from ready_report_indicator where name="ANC new registrations screened for malnutrition (MUAC)"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as ANCMUAC,
	IFNULL((select SUM(value) from ready_report_indicator where name="ANC new registrations screened who were malnourished (MUAC < 21 cm)"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as "ANCMUAC<21",
	IFNULL((select SUM(value) from ready_report_indicator where name="ANC new registrations tested for anemia"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as ANCNewRegAnemia,
	IFNULL((select SUM(value) from ready_report_indicator where name="ANC new registrations with anemia (Moderate and Severe)"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as ANCNewRegAnemiaModAndSev,
	IFNULL((select SUM(value) from ready_report_indicator where name="ANC new registrations syphilis tested"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as ANCNewRegSyphilisTested,
	IFNULL((select SUM(value) from ready_report_indicator where name="ANC new registrations syphilis tested Positive"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as ANCNewRegSyphilisTestedPositive,
	IFNULL((select SUM(value) from ready_report_indicator where name="ANC new registrations received ultrasound scan"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as ANCNewRegUltrasoundScan,
	IFNULL((select SUM(value) from ready_report_indicator where name="Number of pregnant women screened for HBV"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as ANCHBVScreened,
	IFNULL((select SUM(value) from ready_report_indicator where name="Number of pregnant women screened for HBV positive"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as ANCHBVScreenedPositive,
	IFNULL((select SUM(value) from ready_report_indicator where name="Number of pregnant women screened for HBV positive and recent viral load"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as ANCHBVScreenedPositiveRecentVL,
	IFNULL((select SUM(value) from ready_report_indicator where name="women accompanied by their partners during antenatal consultation ANC first visit"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as ANCfirstvisitAccompanied,
	IFNULL((select SUM(value) from ready_report_indicator where name="ANC new registrations who received MMS Supplement"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as ANCNewRegReveivedMMS