select
	(select name from location where location_id in (select parent_location from location where name like "%ANC%" and retired =0) limit 1 ) as location,
	IFNULL((select SUM(value) from ready_report_indicator where name="Deliveries at health facility"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as deliveries,
	IFNULL((select SUM(value) from ready_report_indicator where name="Delivery complicated by perineal tear"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as delivPerinealtear,
	IFNULL((select SUM(value) from ready_report_indicator where name="Cleft palate or Cleft lip"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as cleftpalate,
	IFNULL((select SUM(value) from ready_report_indicator where name="Other congenital malformation"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as othercongmal,
	IFNULL((select SUM(value) from ready_report_indicator where name="Deliveries under 14 years"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as deliunder14yr,
	IFNULL((select SUM(value) from ready_report_indicator where name="Deliveries 14 – 17 years"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as delibtw14and17yrs,
	IFNULL((select SUM(value) from ready_report_indicator where name="Deliveries 18 – 19 years"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as delibtw18and19yrs,
	IFNULL((select SUM(value) from ready_report_indicator where name="Multiple Pregnancies (women who delivered twins, triplets, etc.)"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as multipregn,
	IFNULL((select SUM(value) from ready_report_indicator where name="Women consulted for risk of premature delivery between 24-34 weeks"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as conspreterm,
	IFNULL((select SUM(value) from ready_report_indicator where name="Women who received oxytocin immediately after birth for active management of third stage of labor"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as oxytocinrecieved,
	IFNULL((select SUM(value) from ready_report_indicator where name="Births, live"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as birthlive,
	IFNULL((select SUM(value) from ready_report_indicator where name="Birth weight <2500gr (alive,)"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as birthweightbl2500,
	IFNULL((select SUM(value) from ready_report_indicator where name="Premature newborn (alive, 22-37 weeks)"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as prematurenewborn,
	IFNULL((select SUM(value) from ready_report_indicator where name="Birth Weight <=2000gr(alive newborns)"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as weightbl2000,
	IFNULL((select SUM(value) from ready_report_indicator where name="Still births fresh (>=28 weeks or >=1000grams) all"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as stillbirth28or1000gr,
	IFNULL((select SUM(value) from ready_report_indicator where name="Stillbirths fresh (≥2500 grams)"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as stillbirth2500gr,
	IFNULL((select SUM(value) from ready_report_indicator where name="Deaths at birth of live born babies ( within 30 minutes)"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as deathAtbirth,
	IFNULL((select SUM(value) from ready_report_indicator where name="Newborns breastfed within 1 hour of delivery"  and voided=0 and Date_of_Indicator>=@startDate and Date_of_Indicator <=@endDate), 0) as breastfedin1hour
